import React, { useState } from "react";
import "./chat.css"; // styles for chat button

export default function ChatButton({ visible = true, position = "bottom-right", showLabel = false }) {
  const [open, setOpen] = useState(false);

  if (!visible) return null;

  const posClass = position === "bottom-left" ? "chat-btn-left" : "chat-btn-right";

  return (
    <>
      <div className={`chat-button ${posClass}`} onClick={() => setOpen(v => !v)} role="button" aria-label="Open chat">
        <div className="chat-icon">ðŸ’¬</div>
        {showLabel && <div className="chat-label">Chat with Sales</div>}
      </div>

      {open && (
        <div className={`chat-window ${posClass}`}>
          <div style={{ padding: 8, borderBottom: "1px solid #eee", background: "#fff" }}>
            <strong>Sales chat</strong>
            <button style={{ float: "right" }} onClick={() => setOpen(false)}>Close</button>
          </div>
          <div style={{ padding: 12, height: 200, background: "#fff" }}>
            <p style={{ marginTop: 0 }}>This is a demo chat. Integrate real chat backend or HubSpot live chat here.</p>
          </div>
        </div>
      )}
    </>
  );
}
